{
  "recommended_spec": {
    "title": "Two-Part Structural Epoxy",
    "chemistry": "Modified Bisphenol-A Epoxy with Amine Hardener",
    "example_products": ["Loctite EA 9395", "3M Scotch-Weld DP460"],
    "rationale": "This epoxy system provides excellent adhesion to both aluminum and carbon fiber composite substrates. The modified bisphenol-A resin offers high lap shear strength (35+ MPa) with good resistance to vibration fatigue. The amine hardener enables room-temperature cure with optional heat acceleration, making it suitable for production environments without autoclave access."
  },
  "product_characteristics": {
    "viscosity": "Thixotropic paste, non-sag",
    "work_life": "90 minutes at 25°C (2:1 mix ratio by volume)",
    "fixture_time": "8–12 hours at 25°C, or 1 hour at 66°C",
    "full_cure": "5 days at 25°C or 1 hour at 150°C",
    "service_temp_range": "-55°C to 177°C",
    "lap_shear_aluminum": "35 MPa (ASTM D1002)",
    "lap_shear_composite": "28 MPa (fiber-tear failure mode)"
  },
  "application_guidance": "1. Degrease aluminum surfaces with acetone or MEK wipe. 2. Abrade with 180-grit aluminum oxide paper in cross-hatch pattern. 3. Peel-ply composite surfaces or lightly sand with 320-grit. 4. Apply adhesive within 4 hours of surface preparation. 5. Maintain bondline thickness of 0.10–0.25 mm using glass bead spacers. 6. Apply uniform clamping pressure of 0.05–0.10 MPa. 7. Cure per selected schedule — heat cure strongly recommended for structural applications.",
  "alternatives": [
    {
      "title": "Toughened Epoxy Film Adhesive",
      "chemistry": "Rubber-toughened epoxy film (supported)",
      "example_products": ["Henkel EA 9696", "Solvay FM 300-2"],
      "rationale": "Superior peel strength and damage tolerance. Requires autoclave cure (120°C/175°C at 0.3 MPa). Preferred for primary aerospace structures where peel and impact resistance are critical."
    },
    {
      "title": "Methacrylate Structural Adhesive",
      "chemistry": "Methyl methacrylate (MMA)",
      "example_products": ["Plexus MA310", "Lord 406/19"],
      "rationale": "Faster fixture time (15–20 minutes) and more tolerant of oily or minimally prepared surfaces. Lower service temperature limit (120°C) but excellent for rapid production throughput."
    }
  ],
  "confidence": 87
}
